import React, { useContext, useEffect, useState } from "react";
import { UserContext } from "../../Context/UserContext";
import "./rick-pagination.scss";

const RickPagination: React.FC = () => {
  const userContext = useContext(UserContext);
  const [curNumber, setCurNumber] = useState(1); // Initialize with 1

  function getPageNumberFromUrl(url: any) {
    const urlParams = new URLSearchParams(url);
    const pageNumber = urlParams.get("page");
    return pageNumber ? parseInt(pageNumber) : 1;
  }

  useEffect(() => {
    const curUrl = userContext.currentPageUrl;
    const pageNumber = getPageNumberFromUrl(curUrl);
    setCurNumber(pageNumber); // Update curNumber with current page number
  }, [userContext.currentPageUrl]);

  const nextPage = userContext?.nextPage;
  const prevPage = userContext?.prevPage;
  const goToPage = userContext?.goToPage;
  const pages = userContext?.pages || 0;
  let pageButtons = [];
  for (let i = 1; i <= pages; i++) {
    pageButtons.push(
      <button key={i} onClick={() => goToPage(i)}>
        {i}
      </button>
    );
  }

  return (
    <div className="">
      {prevPage && <button onClick={prevPage}>Previous</button>}
      {pageButtons}
      {nextPage && <button onClick={nextPage}>Next</button>}
      <p>Current Page: {curNumber}</p> {/* Display current page number */}
    </div>
  );
};

export default RickPagination;
