import React, { useState } from "react";
import "./edit-modal.scss";
import { Button, Form, Input } from "antd";

type FieldType = {
  name?: string;
  species?: string;
};
interface EditModalProps {
  handleClose: () => void;
  initialCharacter: FieldType;
  onUpdateCharacter: (updatedCharacter: FieldType) => void;
}

const EditModal: React.FC<EditModalProps> = ({
  handleClose,
  initialCharacter,
  onUpdateCharacter,
}) => {
  const [formValues, setFormValues] = useState<FieldType>(initialCharacter);

  const handleOverlayClick = (event: { target: any; currentTarget: any }) => {
    if (event.target === event.currentTarget) {
      handleClose();
    }
  };

  const onFinish = (values: FieldType) => {
    onUpdateCharacter(values);
    handleClose();
  };

  const onFinishFailed = (errorInfo: any) => {
    console.log("Failed:", errorInfo);
  };

  return (
    <div className="edit-modal" onClick={handleOverlayClick}>
      <Form
        name="basic"
        labelCol={{ span: 8 }}
        wrapperCol={{ span: 16 }}
        style={{ maxWidth: 600 }}
        initialValues={{ ...formValues }}
        onFinish={onFinish}
        onFinishFailed={onFinishFailed}
        autoComplete="off"
      >
        <span className="close-modal" onClick={handleClose}>
          X
        </span>
        <Form.Item<FieldType>
          label="Name"
          name="name"
          rules={[{ required: true, message: "Edit character name" }]}
        >
          <Input />
        </Form.Item>

        <Form.Item<FieldType>
          label="Species"
          name="species"
          rules={[{ required: true, message: "Edit character species" }]}
        >
          <Input />
        </Form.Item>

        <Form.Item wrapperCol={{ offset: 8, span: 16 }}>
          <Button type="primary" htmlType="submit">
            Submit
          </Button>
        </Form.Item>
      </Form>
    </div>
  );
};

export default EditModal;
