import React, { useState } from "react";
import { Button } from "antd";
import { useContext } from "react";
import { UserContext } from "../../Context/UserContext";
import Logo from "../../Assets/logo.png";
import "./header.scss";

interface HeaderProps {
  content?: string;
  favorites?: string;
  home?: string;
}

const Header: React.FC<HeaderProps> = ({ content, favorites, home }) => {
  const userContext = useContext(UserContext);
  const [menuVisible, setMenuVisible] = useState(false);
  const isUserLoggedIn = localStorage.getItem("username" && "password");

  const toggleMenu = () => {
    setMenuVisible(!menuVisible);
  };

  return (
    <header>
      <img src={Logo} alt="Rick And Morty" />
      <div className="menu-button" onClick={toggleMenu}>
        <div className={`menu-icon ${menuVisible ? "open" : ""}`}>
          <span>1</span>
          <span>1</span>
          <span>1</span>
        </div>
      </div>
      {menuVisible && (
        <nav className="menu">
          <p onClick={userContext?.navigateToFavorites}>{favorites}</p>
          <p onClick={userContext?.navigateToChar}>{home}</p>
          {isUserLoggedIn ? (
            <Button onClick={userContext?.navigateToHome} danger type="primary">
              {content}
            </Button>
          ) : null}
        </nav>
      )}
    </header>
  );
};

export default Header;
