import React from "react";
import { useContext } from "react";
import { UserContext } from "../../Context/UserContext";
import Loader from "../Loader";
import "./all-char.scss";
interface AllCharactersProps {
  handleOpen: (char: any) => void;
  searchType: string;
  searchValue: string;
}

const AllCharacters: React.FC<AllCharactersProps> = ({
  handleOpen,
  searchType,
  searchValue,
}) => {
  const userContext = useContext(UserContext);
  const characterData = userContext?.data?.results;
  const inputValue = userContext?.searchValue?.data;
  const filteredByName = inputValue
    ? characterData?.filter((char) =>
        char?.name.toLowerCase().includes(inputValue)
      )
    : characterData;
  if (!userContext?.data) {
    return (
      <div>
        <Loader />
      </div>
    );
  }
  return (
    <div className="div-all-characters">
      {filteredByName?.map((char, i) => (
        <section onClick={() => handleOpen(char)} key={i}>
          <h3>{char.name}</h3>
          <img src={char.image} alt="" />
        </section>
      ))}
    </div>
  );
};

export default AllCharacters;
