import React, { useContext } from "react";
import { UserContext } from "../../Context/UserContext";
import Loader from "../Loader";
import "./all-char.scss";

interface AllCharactersProps {
  handleOpen: (char: any) => void;
}

const AllCharacters: React.FC<AllCharactersProps> = ({ handleOpen }) => {
  const userContext = useContext(UserContext);
  const characterData = userContext?.data?.results;
  const selectedValue = userContext?.selectedValue || "";

  let filteredCharacters = characterData;

  if (userContext?.searchType === "name") {
    filteredCharacters = characterData?.filter((char) =>
      char.name
        .toLowerCase()
        .includes(userContext.searchValue.data.toLowerCase())
    );
  } else if (userContext?.searchType === "species") {
    filteredCharacters = characterData?.filter((char) =>
      char.species
        .toLowerCase()
        .includes(userContext.searchValue.data.toLowerCase())
    );
  }

  if (selectedValue) {
    filteredCharacters = filteredCharacters?.filter((char) =>
      char[selectedValue as keyof Character]
        .toLowerCase()
        .includes(userContext.searchValue.data.toLowerCase())
    );
  }

  if (!userContext?.data) {
    return (
      <div>
        <Loader />
      </div>
    );
  }

  return (
    <div className="div-all-characters">
      {filteredCharacters?.map((char, i) => (
        <section onClick={() => handleOpen(char)} key={i}>
          <h3>{char.name}</h3>
          <img src={char.image} alt="" />
        </section>
      ))}
    </div>
  );
};

export default AllCharacters;
