import React, { useState, useEffect } from "react";
import { UserContext } from "../../Context/UserContext";
import { useContext } from "react";
import "./rick-pagination.scss";

const RickPagination: React.FC = () => {
  const userContext = useContext(UserContext);
  const nextPage = userContext?.nextPage;
  const prevPage = userContext?.prevPage;
  const goToPage = userContext?.goToPage;
  const curUrl = userContext.currentPageUrl;
  const curNumber = parseInt(curUrl.match(/page=(\d+)/)?.[1]) || 1;
  const pages = userContext?.pages || 0;
  const [currentPage, setCurrentPage] = useState(curNumber);

  const pageButtons = [];
  const displayRange = 5;
  const startPage = Math.max(1, currentPage - Math.floor(displayRange / 2));
  const endPage = Math.min(pages, startPage + displayRange - 1);

  for (let i = startPage; i <= endPage; i++) {
    pageButtons.push(
      <button
        key={i}
        onClick={() => {
          setCurrentPage(i);
          goToPage(i);
        }}
        className={i === currentPage ? "current-page-button" : ""}
      >
        {i}
      </button>
    );
  }

  useEffect(() => {
    localStorage.setItem("currentPage", currentPage.toString());
  }, [currentPage]);

  return (
    <div className="">
      {prevPage && <button onClick={prevPage}>Previous</button>}
      {pageButtons}
      {nextPage && <button onClick={nextPage}>Next</button>}
    </div>
  );
};

export default RickPagination;
