import React from "react";
import { UserContext } from "../../Context/UserContext";
import { useContext } from "react";
import { Pagination } from "antd";
import "./rick-pagination.scss";

const RickPagination: React.FC = () => {
  const userContext = useContext(UserContext);
  const nextPage = userContext?.nextPage;
  const prevPage = userContext?.prevPage;
  const goToPage = userContext?.goToPage;
  const currentPage = Number(userContext?.currentPageUrl) || 1; // Convert to number
  const totalPages = userContext?.pages || 0;

  const handlePaginationChange = (page: number) => {
    if (page !== currentPage) {
      goToPage(page);
    }
  };

  return (
    <div className="rick-pagination-div">
      <Pagination
        defaultCurrent={1}
        total={totalPages * 20} // Assuming pageSize is 20
        pageSize={20}
        current={currentPage}
        onChange={handlePaginationChange}
        showSizeChanger={false} // You can configure this as needed
      />
      {prevPage && <button onClick={prevPage}>Previous</button>}
      {nextPage && <button onClick={nextPage}>Next</button>}
    </div>
  );
};

export default RickPagination;
