import React, { useState } from "react";
import Header from "../../Components/Header";
import AllCharacters from "../../Components/AllCharacters";
import Footer from "../../Components/Footer";
import CharModal from "../../Components/CharModal";
import SearchInput from "../../Components/SearchInput";
import "./home-page.scss";

const HomePage: React.FC = () => {
  const [modal, setModal] = useState(false);
  const [clickedChar, setClickedChar] = useState(null);

  const [searchType, setSearchType] = useState(""); // Add searchType state

  const handleOpen = (char: React.SetStateAction<null>) => {
    setClickedChar(char);
    setModal(true);
  };

  const handleClose = () => {
    setModal(false);
  };

  const handleSearchByName = (input: string) => {
    setSearchType("name");
  };

  const handleSearchBySpecies = (input: string) => {
    setSearchType("species");
  };

  return (
    <>
      <Header content="Logout" favorites="Favorites" />

      {/* Pass the appropriate search type to SearchInput components */}
      <SearchInput placeholder="Search by Name" onSearch={handleSearchByName} />
      <SearchInput
        placeholder="Search by Species"
        onSearch={handleSearchBySpecies}
      />

      <div className="div-home-page">
        {/* Pass the searchType to AllCharacters component */}
        <AllCharacters handleOpen={handleOpen} searchType={searchType} />
        {modal && (
          <CharModal clickedChar={clickedChar} handleClose={handleClose} />
        )}
      </div>
      <Footer />
    </>
  );
};

export default HomePage;
