import React from "react";
import { UserContext } from "../../Context/UserContext";
import { useContext } from "react";
import "./rick-pagination.scss";

const RickPagination: React.FC = () => {
  const userContext = useContext(UserContext);
  const goToPage = userContext?.goToPage;
  const currentPage = Number(userContext?.currentPageUrl) || 1;
  const totalPages = userContext?.pages || 0;

  // Calculate the start and end page numbers to display
  let startPage = Math.max(1, currentPage - 2);
  let endPage = Math.min(totalPages, startPage + 4);
  if (endPage - startPage < 4) {
    startPage = Math.max(1, endPage - 4);
  }

  const handlePaginationChange = (page: number) => {
    if (page !== currentPage) {
      goToPage(page);
    }
  };

  return (
    <div>
      {currentPage > 1 && (
        <button onClick={() => handlePaginationChange(currentPage - 1)}>
          Previous
        </button>
      )}
      {Array.from(
        { length: endPage - startPage + 1 },
        (_, i) => i + startPage
      ).map((page) => (
        <button
          key={page}
          onClick={() => handlePaginationChange(page)}
          className={currentPage === page ? "current-page" : ""}
        >
          {page}
        </button>
      ))}
      {currentPage < totalPages && (
        <button onClick={() => handlePaginationChange(currentPage + 1)}>
          Next
        </button>
      )}
    </div>
  );
};

export default RickPagination;
