import React, { useState } from "react";
import { useContext } from "react";
import { UserContext } from "../../Context/UserContext";
import Loader from "../Loader";
import Modal from "antd/es/modal/Modal";
import "./all-char.scss";

const AllCharacters = () => {
  const userContext = useContext(UserContext);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const onModalCloseRequest = (): void => {
    setIsModalOpen(false);
  };
  const characterNames = userContext?.data?.results;
  console.log(characterNames);
  if (!userContext?.data) {
    return (
      <div>
        <Loader />
      </div>
    );
  }
  return (
    <div className="div-all-characters">
      {characterNames?.map((char, i) => (
        <section onClick={setIsModalOpen} key={i}>
          <h3>{char.name}</h3>
          <img src={char.image} alt="" />
          <Modal isOpen={isModalOpen} onCloseRequest={onModalCloseRequest} />
        </section>
      ))}
    </div>
  );
};

export default AllCharacters;
