import React, { useState } from "react";
import Header from "../../Components/Header";
import AllCharacters from "../../Components/AllCharacters";
import Footer from "../../Components/Footer";
import CharModal from "../../Components/CharModal";
import "./home-page.scss";

const HomePage: React.FC = () => {
  const [modal, setModal] = useState(false);
  const [clickedChar, setClickedChar] = useState(null); // Initialize with null

  const handleOpen = (char) => {
    setModal(true);
    setClickedChar(char); // Store the clicked character object
  };

  const handleClose = () => {
    setModal(false);
    setClickedChar(null); // Reset the clicked character object when closing modal
  };

  return (
    <>
      <Header content="Logout" favorites="Favorites" />
      <div className="div-home-page">
        <AllCharacters handleOpen={handleOpen} />
        {modal && (
          <CharModal handleClose={handleClose} clickedChar={clickedChar} />
        )}
      </div>
      <Footer />
    </>
  );
};

export default HomePage;
