import React from "react";
import { UserContext } from "../../Context/UserContext";
import { useContext } from "react";
import "./rick-pagination.scss";

const RickPagination: React.FC = () => {
  const userContext = useContext(UserContext);
  const nextPage = userContext?.nextPage;
  const prevPage = userContext?.prevPage;
  const goToPage = userContext?.goToPage;
  const currentPage = Number(userContext?.currentPageUrl) || 1;
  const totalPages = userContext?.pages || 0;
  const visiblePages = 5; // Number of visible page buttons
  const halfVisible = Math.floor(visiblePages / 2);

  const handlePaginationChange = (page: number) => {
    if (page !== currentPage) {
      goToPage(page);
    }
  };

  let startPage = Math.max(1, currentPage - halfVisible);
  let endPage = Math.min(totalPages, startPage + visiblePages - 1);
  if (endPage - startPage + 1 < visiblePages) {
    startPage = Math.max(1, endPage - visiblePages + 1);
  }

  const pageButtons = Array.from(
    { length: endPage - startPage + 1 },
    (_, i) => i + startPage
  );

  return (
    <div>
      {prevPage && <button onClick={prevPage}>Previous</button>}
      {pageButtons.map((page) => (
        <button
          key={page}
          onClick={() => handlePaginationChange(page)}
          className={currentPage === page ? "current-page" : ""}
        >
          {page}
        </button>
      ))}
      {nextPage && <button onClick={nextPage}>Next</button>}
    </div>
  );
};

export default RickPagination;
